<form class='add-form' id='collectibleBookForm' action='addcollectiblebook.php' method="post" onsubmit="return validateAddCollectibleBook()">    <table class='add-form'>        <tr>            <td>                 <label><span>Title</span></label>            </td>            <td>                <input type='text' class='add-input' name='collectibleBookTitle' id='collectibleBookTitle' />            </td>            <td class="add-form error-message" id='collectibleBookTitleError' style="text-align:left;"></td>        </tr>        <tr>            <td>              <label><span>Author</span></label>            </td>            <td>                <input type='text' class='add-input' name='collectibleBookAuthor' id='collectibleBookAuthor' /><br />            </td>            <td class="add-form error-message" id='collectibleBookAuthorError' style="text-align:left;"></td>        </tr>        <tr>            <td>                <label><span>Publish Date</span></label>            </td>            <td style="text-align:left;">                <input type='text' class='add-input-year' name='collectibleBookPublishDateYear'                     id='collectibleBookPublishDateYear' placeholder='YYYY' /><span> / </span><input type='text'                     class='add-input-month-day' name='collectibleBookPublishDateMonth' id='collectibleBookPublishDateMonth'                     placeholder='MM' /><span> / </span><input type='text'                     class='add-input-month-day' name='collectibleBookPublishDateDay' id='collectibleBookPublishDateDay'                     placeholder='DD' />            </td>            <td class="add-form error-message" id='collectibleBookPublishDateError' style="text-align:left;"></td>        </tr>        <tr>            <td>                <label><span>Market Price</span></label>            </td>            <td>                <input type='text' class='add-input' name='collectibleBookPrice' id='collectibleBookPrice' />            </td>            <td class="add-form error-message" id='collectibleBookPriceError' style="text-align:left;"></td>        </tr>                <tr>            <td>                <label><span>Language</span></label>            </td>            <td>                <input type='text' class='add-input' name='collectibleBookLanguage' id='collectibleBookLanguage' />            </td>            <td class="add-form error-message" id='collectibleBookLanguageError' style="text-align:left;"></td>        </tr>        <tr>            <td>                <label><span>Condition</span></label>            </td>            <td>                <?php conditionList("collectibleBookConditionGrade"); ?>            </td>            <td class="add-form error-message" id='collectibleBookConditionGradeError' style="text-align:left;"></td>        </tr>        <tr>            <td>                <label><span>Acquisition Price</span></label>            </td>            <td>                <input type='text' class='add-input' name='collectibleBookAcquisitionPrice' id='collectibleBookAcquisitionPrice' />            </td>            <td class="add-form error-message" id='collectibleBookAcquisitionPriceError' style="text-align:left;"></td>        </tr>        <tr>            <td>                <label><span>In Stock</span></label>            </td>            <td style="text-align:left;">                <input type="radio" name="collectibleBookInStock" id="collectibleBookInStock_Yes" value="true" checked>                <label for="collectibleBookInStock_Yes"><span>Yes</span></label>                &nbsp;<input type="radio" name="collectibleBookInStock" id="collectibleBookInStock_No" value="false">                <label for="collectibleBookInStock_No"><span>No</span></label>            </td>            <td class="add-form error-message" id='collectibleBookInStockError' style="text-align:left;"></td>        </tr>        <tr>            <td>                <label><span>Book Description</span></label>            </td>            <td>                <textarea class='add-textarea' name='collectibleBookDescription' id='collectibleBookDescription'                     rows="5" ></textarea>            </td>            <td class="add-form error-message" id='collectibleBookDescriptionError' style="text-align:left;"></td>        </tr>        <tr>            <td>                <label><span>Condition Description</span></label>            </td>            <td>                <textarea class='add-textarea' name='collectibleBookConditionDescription' id='collectibleBookConditionDescription'                     rows="5" ></textarea>            </td>            <td class="add-form error-message" id='collectibleBookConditionDescriptionError' style="text-align:left;"></td>        </tr>        <tr>            <td>                &nbsp;            </td>            <td class='add-button-cell'>                <input type='submit' class='add-button' value='Submit' name='collectibleBookSubmit' />            </td>            <td>                &nbsp;            </td>        </tr>    </table></form>